function lessText(){$(".less_text").each(function(e){if($(this).html().length>40){var s=$(this).text().substr(0,40),t=$(this).text().substr(40);t=t.replace(/(?:\r\n|\r|\n)/g,"<br />"),t+='<a href="#" class="badge read_less">Read less</a>';$(this).html(s+'<span class="between_content">... </span><a href="#" class="badge read_more">Read more</a><span class="more_text" style="display:none;">'+t+"</span>"),$(this).find("a.read_more").click(function(e){e.preventDefault(),$(this).hide(),$(this).parents(".less_text").find("a.read_less").show(),$(this).parents(".less_text").find(".between_content").hide(),$(this).parents(".less_text").find(".more_text").show()}),$(this).find("a.read_less").click(function(e){e.preventDefault(),$(this).hide(),$(this).parents(".less_text").find("a.read_more").show(),$(this).parents(".less_text").find(".between_content").show(),$(this).parents(".less_text").find(".more_text").hide()})}})}$(document).on("ready pjax:success",function(){lessText()}),lessText();
